mapper.select:

SELECT 



FROM
	tb_code A
WHERE	1=1
AND	A.code_use_yn = 'Y'


 AND A.code_id = #{codeId} 


 AND A.code_name = #{codeName} 


 AND A.parent_code_id = #{parentCode}

SELECT
FROM tb_code A WHERE 1=1
AND A.code_id = #{codeId}

mapper.update:

UPDATE tb_code
SET 
		code_name		= #{codeName}
		,code_order		= #{codeOrder}
		,code_use_yn	= #{codeUseYn}
		,register_date	= NOW()
		,register_id	= #{updateId}
		,register_ip	= #{updateIp}
WHERE	code_id = #{codeId}
